import React from 'react';
import logo from './logo.svg';
import './App.css';

let rowLimit = 100;

class App extends React.Component {
	constructor( props ){
		super( props );  
		this.state = {
			data: []
		};
	}	

	render() {
		const rows = []
		for ( let i = 0; i < rowLimit; i++ ) {
			rows.push( <Row data={this.state.data[i]} /> )
		};
		return <table className="launchTable">
			<Row header={true} > </Row>
			{ rows }
		</table>
	}

	componentDidMount() {
		fetch("https://api.spacexdata.com/v3/launches")
		.then(response => response.json())
		.then(data => this.setState({ data }));
	}
}

let tableHeader = <tr> 
					<th> Mission </th> 
					<th> Date </th> 
					<th> Rocket </th> 
					<th> Payload </th>
					<th> Launch Success </th>
				</tr>

class Row extends React.Component {
	constructor( props ){
		super( props );  
		this.state = {
			data: []
		};
	}

	render() {
		if ( this.props.header ) return tableHeader;
		if ( this.props.data ) {
			const data = this.props.data
			return <tr>
						<td> { missionName( data ) } </td>
						<td> { centralDate( data ) } </td>
						<td> { rocketName( data ) } </td>
						<td> { cargo( data ) } </td>
						<td> { launchSuccess( data ) } </td>
					</tr>
		}
		return "loading";
	}
}

function missionName( data ) {
	return data.mission_name
}

function centralDate( data ) {
	const date = new Date( data.launch_date_utc );
	let str = date.getMonth() + '/' + date.getDay() + '/' + date.getFullYear() + ' - ' + date.getTime()
	return str
}

function rocketName( data ) {
	return data.rocket.rocket_name
}

function cargo( data ) {
	if (!data.rocket.second_stage.payloads) return "none";
	let payloads = data.rocket.second_stage.payloads

	let payloadData = []
	for ( const i in payloads ){
		if ( i > 0 ) payloadData.push( " / " )
		let txt = payloads[i].payload_id;
		let link = payloads[i].cargo_manifest
		payloadData.push( payloads[i].cargo_manifest ? <a href={link}> {txt} </a> : txt )
	}
	return payloadData
}

function launchSuccess( data ) {
	if ( data.launch_success ) return "Successful";
	if ( data.launch_failure_details && data.launch_failure_details.reason ) return "Failure: " + data.launch_failure_details.reason;
	return "Failure"
}

// ========================================

// ReactDOM.render(
  // <App />,
  // document.getElementById('root')
// );





export default App;
