import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {
	constructor( props ){
		super( props );  
		this.state = {
			data: []
		};
	}	

	render() {
		return <table className="launchTable">
			<Row data={this.state.data[0]} header={true} > </Row>
			<Row data={this.state.data[1]} > </Row>
			<Row data={this.state.data[2]} > </Row>
			<Row data={this.state.data[3]} > </Row>
			<Row data={this.state.data[4]} > </Row>
		</table>
	}

	componentDidMount() {
		fetch("https://api.spacexdata.com/v3/launches")
		.then(response => response.json())
		.then(data => this.setState({ data }));
	}
}

let tableHeader = <tr> 
					<th> "Mission" </th> 
					<th> "Flight #" </th> 
					<th> "Rocket" </th> 
					<th> "Cargo" </th>
					<th> "Success" </th>
				</tr>

class Row extends React.Component {
	constructor( props ){
		super( props );  
		this.state = {
			data: []
		};
	}

	render() {
		if ( this.props.header ) return tableHeader;
		if ( this.props.data ) {
			return FlightNum( this.props.data )
		}
		return "loading";
	}
}

function FlightNum( data ) {
return <td> { data.flight_number } </td>;
}

// ========================================

// ReactDOM.render(
  // <App />,
  // document.getElementById('root')
// );





export default App;
