import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {
	constructor( props ){
		super( props );  
		this.state = {
			data: []
		};
	}	

	render() {
		if ( this.state.data[0] ) {
			let str = "Flight: ";
			str = str + this.state.data[0].flight_number;
			return str;
		}
		return "loading";
	}

	componentDidMount() {
		fetch("https://api.spacexdata.com/v3/launches")
		.then(response => response.json())
		.then(data => this.setState({ data }));
	}
}

class Row extends React.Component {
	constructor( props ){
		super( props );  
		this.state = {
			data: []
		};
	}
	render() {
		if ( this.props.data ) {
			let str = "Flight: ";
			str = str + this.props.data.flight_number;
			return str;
		}
		return "loading";
	}
}



// ========================================

// ReactDOM.render(
  // <App />,
  // document.getElementById('root')
// );





export default App;
