import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {
	constructor( props ){
		super( props );  
		this.state = {
			data: []
		};
	}	

	render() {
		return <div className="launchTable">
			<Row data={this.state.data[0]} > </Row>
			<Row data={this.state.data[1]} > </Row>
			<Row data={this.state.data[2]} > </Row>
			<Row data={this.state.data[3]} > </Row>
			<Row data={this.state.data[4]} > </Row>
		</div>
	}

	componentDidMount() {
		fetch("https://api.spacexdata.com/v3/launches")
		.then(response => response.json())
		.then(data => this.setState({ data }));
	}
}

class Row extends React.Component {
	constructor( props ){
		super( props );  
		this.state = {
			data: []
		};
	}
	render() {
		if ( this.props.data ) {
			let str = "Flight: ";
			str = str + this.props.data.flight_number;
			return <grid className="board-row"> {str} </grid>;
		}
		return "loading";
	}
}

