import React from 'react';
import logo from './logo.svg';
import './App.css';


let API = "https://api.spacexdata.com/v3/launches"
let queryString = "?sort=flight_number&order=desc&filter=mission_name,launch_date_utc,launch_failure_details/reason,launch_success,upcoming,rocket";
let rowLimit = 100;
let tableHeader = <tr> 
					<th> Mission </th> 
					<th> Date </th> 
					<th> Rocket </th> 
					<th> Payload </th>
					<th> Launch Status </th>
				</tr>

class App extends React.Component {
	constructor( props ){
		super( props );  
		this.state = {
			data: []
		};
	}	

	render() {
		const rows = []
		for ( let i = 0; i < rowLimit; i++ ) {
			rows.push( <Row data={ this.state.data[i] }/> )
		};
		return <table className="launchTable" border="20px" border-collapse="collapse">
			{ tableHeader }
			{ rows }
		</table>
	}

	componentDidMount() {
		fetch( API + queryString )
		.then(response => response.json())
		.then(data => this.setState({ data }));
	}
}

class Row extends React.Component {
	constructor( props ){
		super( props );  
		this.state = {
			data: []
		};
	}

	render() {
		if ( this.props.data ) {
			const data = this.props.data
			return <tr>
						<td> { missionName( data ) } </td>
						<td> { centralDate( data ) } </td>
						<td> { rocketName( data ) } </td>
						<td> { cargo( data ) } </td>
						<td> { launchStatus( data ) } </td>
					</tr>
		}
		return "loading";
	}
}

function missionName( data ) {
	return data.mission_name
}

function centralDate( data ) {
	const date = new Date( data.launch_date_utc );
	let str = date.toDateString() + ' - ' + date.getHours() + ':' + ( date.getMinutes() )
	return str
}

function rocketName( data ) {
	return data.rocket.rocket_name
}

function cargo( data ) {
	if (!data.rocket.second_stage.payloads) return "none";
	let payloads = data.rocket.second_stage.payloads

	let payloadData = []
	for ( const i in payloads ){
		if ( i > 0 ) payloadData.push( " / " )
		let txt = payloads[i].payload_id;
		let link = payloads[i].cargo_manifest
		payloadData.push( payloads[i].cargo_manifest ? <a href={link}> {txt} </a> : txt )
	}
	return payloadData
}

function launchStatus( data ) {
	if ( data.launch_success ) return "Successful";
	if ( data.upcoming ) return "Upcoming";
	if ( data.launch_failure_details && data.launch_failure_details.reason ) return "Failure: " + data.launch_failure_details.reason;
	return "Failure"
}

export default App;
